Phaserfroot.PluginManager.register(
  "Mountain_Name_gfbjs",
  class Mountain_Name_gfbjs extends Phaserfroot.Component {
    constructor( target, instanceProperties ) {
      super( {
        name: "Mountain_Name_gfbjs",
        owner: target,
      } );
      this.instanceProperties = instanceProperties;
      this.scene = target.scene;
      this.game = target.scene.game;

      this.owner.once( "levelSwitch", this.destroy, this );

      // Attach custom event listeners.
      this.owner.on( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );


      // Initialize properties from parameters.
      this.player = ( typeof instanceProperties[ "player" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "player" ], true ) : null;


      // Boot phase.

      this.onCreate();

    }

    // BUILT-IN METHODS

    preUpdate () {

    }

    update () {

    }

    postUpdate () {

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      this.owner.removeListener( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );

    }

    // CUSTOM METHODS

    onCreate () {
      // Executed when this script is initially created.
      this.owner.setPhysics( false );
      this.owner.visible = false;
      this.game.GLOBAL_VARIABLES.maunga = '';
    }

    overlapping ( ins1, ins2 ) {
      // Get the most appropriate hitboxes and compare them.
      if( !ins1 || !ins2 || !ins1.getHitbox || !ins2.getHitbox ) {
        return false;
      }
      var box1 = ins1.getHitbox();
      var box2 = ins2.getHitbox();
      var intersection = Phaser.Geom.Rectangle.Intersection( box1, box2 );
      return !!( intersection.width && intersection.height );
    }

    onLevelStart2() {
      this.player = this.scene.getChildrenByTag( 'player' )[ 0 ];
      var componentForeverFrames = new Phaserfroot.Component( { owner: this.owner, name: "componentForeverFrames" } );
      var componentUpdate = function() {
        if (this.overlapping( this.owner, this.player )) {
          this.scene.broadcast( 'show maunga' );
        }

      };
      componentForeverFrames.update = componentUpdate.bind( this );
      this.owner.components.add( componentForeverFrames );
      componentForeverFrames.update();


    }

  }
);

