      this.owner.setAnimationListener("animationrepeat", 'left pressing', this._left_pressing__22animationrepeat_222, this);
      this.owner.setAnimationListener("animationrepeat", 'right pressing', this._right_pressing__22animationrepeat_222, this);
      this.owner.setAnimationListener("animationrepeat", 'up pressing', this._up_pressing__22animationrepeat_222, this);
