        textfield.width = 0;
      } else {
        textfield.displayOriginX = textfield.width * 0;
      }
      if ( !textfield ) {
        this.reportError( "`Change Instance Depth` block could not find the instance [textfield]." );
        return;
      }
      this.scene.addChildAfter( textfield, this.owner );
      if (this.My_Marae.length > 25) {
